// Handle both notebook sources and feed sources
let notebook_id = null;
let source_id;
let user_id;
let context;

try {
  const webhookData = $('Webhook').first();
  if (webhookData && webhookData.json?.body) {
    const body = webhookData.json.body;
    source_id = body.source_id;
    user_id = body.user_id;
    context = body.context;

    // Check if this is explicitly a feed source
    if (context === 'feed') {
      // Explicit feed source - notebook_id stays null
      notebook_id = null;
    } else if (!context && !body.notebook_id) {
      // Implicit feed source: no context and no notebook_id provided
      notebook_id = null;
    } else if (body.file_path) {
      // Extract notebook_id from file path for notebook sources
      const path = body.file_path;
      notebook_id = path.split('/')[0];
    }
  }
} catch (e) {
  // Fallback: get from input (for backwards compatibility)
  const input = $input.first().json;
  notebook_id = input.notebook_id || null;
  source_id = input.source_id;
  user_id = input.user_id;
}

const extracted_text = $input.first().json.extracted_text;

// Explicitly construct the return object to ensure notebook_id is always included
const returnData = {
  notebook_id: notebook_id,
  extracted_text: extracted_text,
  source_id: source_id,
  user_id: user_id
};

return [
  {
    json: returnData
  }
];

